import React, { createContext, useState, useContext, useEffect, useCallback } from 'react';
import { fetchQuestions as apiFetchQuestions, submitAssessment as apiSubmitAssessment } from '../services/api';
import { useNavigate } from 'react-router-dom';

const AssessmentContext = createContext();

export const useAssessment = () => useContext(AssessmentContext);

export const AssessmentProvider = ({ children }) => {
    const [questions, setQuestions] = useState([]);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [userAnswers, setUserAnswers] = useState({});
    const [questionStatus, setQuestionStatus] = useState({});
    const [timer, setTimer] = useState(60 * 60);
    const [isAssessmentStarted, setIsAssessmentStarted] = useState(false);
    const [isAssessmentSubmitted, setIsAssessmentSubmitted] = useState(false);
    const [submissionResult, setSubmissionResult] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
    const [userId, setUserIdState] = useState(localStorage.getItem('assessmentUserId') || '');

    const navigate = useNavigate();

    // Memoized: Initializes all question statuses to 'not-visited'
    const initializeQuestionStatus = useCallback((qs) => {
        const initialStatus = {};
        qs.forEach(q => {
            initialStatus[q._id] = 'not-visited';
        });
        setQuestionStatus(initialStatus);
    }, []); // setQuestionStatus is stable

    // Memoized: Updates a single question's status to 'visited' if it was 'not-visited'
    // Returns previous state reference if no change is made to prevent unnecessary re-renders
    const updateQuestionStatusToVisited = useCallback((questionId) => {
        setQuestionStatus(prevStatus => {
            if (prevStatus[questionId] === 'not-visited') {
                return { ...prevStatus, [questionId]: 'visited' };
            }
            return prevStatus; // Return same object if no change
        });
    }, []); // setQuestionStatus is stable

    const fetchQuestions = useCallback(async () => {
        if (!userId) {
            navigate('/');
            return;
        }
        setIsLoading(true);
        setError(null);
        try {
            const response = await apiFetchQuestions();
            const fetchedQuestions = response.data;
            if (fetchedQuestions && fetchedQuestions.length > 0) {
                setQuestions(fetchedQuestions);
                initializeQuestionStatus(fetchedQuestions); // Set all to not-visited
                setUserAnswers({});
                setIsAssessmentSubmitted(false);
                setSubmissionResult(null);
                setTimer(60 * 60);
                setCurrentQuestionIndex(0); // Explicitly set to 0
                // Mark the *first* question as visited after initialization
                updateQuestionStatusToVisited(fetchedQuestions[0]._id);
            } else {
                setError("No questions found for the assessment.");
                setQuestions([]);
            }
        } catch (err) {
            setError(err.response?.data?.message || 'Failed to fetch questions.');
            console.error("Fetch questions error:", err);
        } finally {
            setIsLoading(false);
        }
    }, [userId, navigate, initializeQuestionStatus, updateQuestionStatusToVisited]); // Added dependencies

    const startAssessment = (id) => {
        setUserIdState(id);
        localStorage.setItem('assessmentUserId', id);
        setIsAssessmentStarted(true);
        // fetchQuestions will be called by AssessmentPage's useEffect or similar trigger
        navigate('/assessment');
    };

    // Memoized: Handles selecting an answer
    const selectAnswer = useCallback((questionId, selectedOption) => {
        setUserAnswers(prev => ({ ...prev, [questionId]: selectedOption }));
        setQuestionStatus(prevStatus => ({ ...prevStatus, [questionId]: 'answered' }));
    }, []); // set... functions are stable

    // Memoized: Navigation functions
    const nextQuestion = useCallback(() => {
        if (currentQuestionIndex < questions.length - 1) {
            const nextIdx = currentQuestionIndex + 1;
            updateQuestionStatusToVisited(questions[nextIdx]._id);
            setCurrentQuestionIndex(nextIdx);
        }
    }, [currentQuestionIndex, questions, updateQuestionStatusToVisited]);

    const prevQuestion = useCallback(() => {
        if (currentQuestionIndex > 0) {
            const prevIdx = currentQuestionIndex - 1;
            updateQuestionStatusToVisited(questions[prevIdx]._id);
            setCurrentQuestionIndex(prevIdx);
        }
    }, [currentQuestionIndex, questions, updateQuestionStatusToVisited]);

    const jumpToQuestion = useCallback((index) => {
        if (index >= 0 && index < questions.length) {
            updateQuestionStatusToVisited(questions[index]._id);
            setCurrentQuestionIndex(index);
        }
    }, [questions, updateQuestionStatusToVisited]);

    const submitAssessment = useCallback(async (submissionStatus = 'completed') => {
        if (isAssessmentSubmitted) return;
        setIsLoading(true);
        setError(null);
        const answersToSubmit = questions.map(q => ({
            questionId: q._id,
            selectedOption: userAnswers[q._id] || null
        }));

        try {
            const response = await apiSubmitAssessment({
                userId,
                answers: answersToSubmit,
                timeTaken: (60 * 60) - timer,
                status: submissionStatus
            });
            setSubmissionResult(response.data);
            setIsAssessmentSubmitted(true);
            localStorage.removeItem('assessmentUserId');
            navigate('/assessment/result');
        } catch (err) {
            setError(err.response?.data?.message || 'Failed to submit assessment.');
            console.error("Submit assessment error:", err);
            if (err.response?.status === 403) {
                setSubmissionResult({ message: err.response.data.message, score: err.response.data.score });
                setIsAssessmentSubmitted(true);
                navigate('/assessment/result');
            }
        } finally {
            setIsLoading(false);
        }
    }, [userId, questions, userAnswers, timer, navigate, isAssessmentSubmitted]);

    // Timer useEffect
    useEffect(() => {
        let interval;
        if (isAssessmentStarted && !isAssessmentSubmitted && timer > 0) {
            interval = setInterval(() => {
                setTimer(prevTimer => prevTimer - 1);
            }, 1000);
        } else if (timer === 0 && isAssessmentStarted && !isAssessmentSubmitted) {
            submitAssessment('timed-out');
        }
        return () => clearInterval(interval);
    }, [isAssessmentStarted, isAssessmentSubmitted, timer, submitAssessment]);

    // REMOVED the problematic useEffect that was here.
    // The logic to update question status to 'visited' is now handled by:
    // 1. `fetchQuestions` for the first question.
    // 2. `nextQuestion`, `prevQuestion`, `jumpToQuestion` for subsequent navigations.
    // 3. Optionally, the useEffect in AssessmentPage.js can also call `updateQuestionStatusToVisited`
    //    if `currentQuestionIndex` changes due to other reasons, ensuring the status is updated.

    const resetAssessment = useCallback(() => { // Also memoize reset
        setQuestions([]);
        setCurrentQuestionIndex(0);
        setUserAnswers({});
        setQuestionStatus({});
        setTimer(60 * 60);
        setIsAssessmentStarted(false);
        setIsAssessmentSubmitted(false);
        setSubmissionResult(null);
        // Consider if userId should be reset here or handled by StudentEntryPage
        // setUserIdState('');
        // localStorage.removeItem('assessmentUserId');
        setIsLoading(false);
        setError(null);
    }, []);


    return (
        <AssessmentContext.Provider value={{
            userId,
            questions,
            currentQuestionIndex,
            userAnswers,
            questionStatus,
            timer,
            isAssessmentStarted,
            isAssessmentSubmitted,
            submissionResult,
            isLoading,
            error,
            fetchQuestions, // Make sure this is called when assessment starts
            startAssessment,
            selectAnswer,
            nextQuestion,
            prevQuestion,
            jumpToQuestion,
            submitAssessment,
            resetAssessment,
            // updateQuestionStatusToVisited, // Not directly exposed if handled internally
            // No longer need setCurrentQuestionIndex directly if jumpToQuestion is used
        }}>
            {children}
        </AssessmentContext.Provider>
    );
};
