import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAssessment } from '../../context/AssessmentContext';
import './StudentEntryPage.css';
import { Link } from 'react-router-dom';

function StudentEntryPage() {
    const [tempUserId, setTempUserId] = useState('');
    const { startAssessment, userId: contextUserId, isAssessmentStarted, resetAssessmentContextState } = useAssessment();
    const navigate = useNavigate();

    useEffect(() => {
        // console.log("StudentEntryPage useEffect:", { contextUserId, isAssessmentStarted });
        // If an assessment was already started (e.g., browser refresh on /assessment)
        // and we have a userId from localStorage (which context initializes with),
        // redirect to assessment.
        // This also handles the case where user reloads on entry page but has a started assessment.
        if (isAssessmentStarted && contextUserId) {
            // console.log("StudentEntryPage: Active assessment found, redirecting to /assessment");
            navigate('/assessment');
        } else {
            // Otherwise, ensure any lingering assessment state is cleared.
            // This is important if user navigates back to entry page after starting.
            // console.log("StudentEntryPage: No active assessment or userId, resetting context state.");
            resetAssessmentContextState();
        }
    }, [contextUserId, isAssessmentStarted, navigate, resetAssessmentContextState]);


    const handleSubmit = (e) => {
        e.preventDefault();
        if (tempUserId.trim()) {
            // console.log("StudentEntryPage: handleSubmit - Starting assessment for:", tempUserId.trim());
            startAssessment(tempUserId.trim());
        } else {
            alert("Please enter your Name or ID.");
        }
    };

    return (
        <div className="student-entry-container">
            <h2>Welcome to the Assessment</h2>
            <p>Please enter your Name or Student ID to begin.</p>
            <form onSubmit={handleSubmit} className="student-entry-form">
                <input
                    type="text"
                    value={tempUserId}
                    onChange={(e) => setTempUserId(e.target.value)}
                    placeholder="Enter Your Name/ID"
                    required
                    className="student-entry-input"
                />
                <button type="submit" className="student-entry-button">
                    Start Assessment
                </button>
            </form>
            <Link to="/admin" className="btn btn-secondary" style={{marginTop: '20px'}}>Admin Panel</Link>
        </div>
    );
}
// Need to import Link for the Admin Panel button
export default StudentEntryPage;
