import React from 'react';
import './LoadingSpinner.css';

function LoadingSpinner({ message = "Loading..." }) {
    return (
        <div className="loading-spinner-overlay">
            <div className="loading-spinner"></div>
            <p>{message}</p>
        </div>
    );
}
export default LoadingSpinner;
