import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAssessment } from '../../context/AssessmentContext';
import './StudentEntryPage.css';

function StudentEntryPage() {
    const [tempUserId, setTempUserId] = useState('');
    const { startAssessment, userId, isAssessmentStarted, resetAssessment } = useAssessment();
    const navigate = useNavigate();

    useEffect(() => {
        // If already started and has a user ID, redirect to assessment
        if (isAssessmentStarted && userId) {
            navigate('/assessment');
        } else {
          // Reset any previous assessment state if landing here without an active session
          resetAssessment();
        }
    }, [isAssessmentStarted, userId, navigate, resetAssessment]);

    const handleSubmit = (e) => {
        e.preventDefault();
        if (tempUserId.trim()) {
            startAssessment(tempUserId.trim());
        } else {
            alert("Please enter your Name or ID.");
        }
    };

    return (
        <div className="student-entry-container">
            <h2>Welcome to the Assessment</h2>
            <p>Please enter your Name or Student ID to begin.</p>
            <form onSubmit={handleSubmit} className="student-entry-form">
                <input
                    type="text"
                    value={tempUserId}
                    onChange={(e) => setTempUserId(e.target.value)}
                    placeholder="Enter Your Name/ID"
                    required
                    className="student-entry-input"
                />
                <button type="submit" className="student-entry-button">
                    Start Assessment
                </button>
            </form>
        </div>
    );
}

export default StudentEntryPage;
